"""Robust UpdateExpression parser and evaluator for DynamoDB.

Supports:
- SET: regular assignment, if_not_exists(path, value), list_append(list1, list2),
  arithmetic (+, -)
- REMOVE: remove attributes
- ADD: add to number or set
- DELETE: delete from set
- Combined clauses: SET ... REMOVE ... ADD ... DELETE ...
"""

from __future__ import annotations

from dataclasses import dataclass, field
from typing import Any

# ---------------------------------------------------------------------------
# Token types
# ---------------------------------------------------------------------------

TOKEN_IDENT = "IDENT"
TOKEN_VALUE_REF = "VALUE_REF"
TOKEN_NAME_REF = "NAME_REF"
TOKEN_NUMBER = "NUMBER"
TOKEN_COMMA = "COMMA"
TOKEN_LPAREN = "LPAREN"
TOKEN_RPAREN = "RPAREN"
TOKEN_EQUALS = "EQUALS"
TOKEN_PLUS = "PLUS"
TOKEN_MINUS = "MINUS"
TOKEN_SET = "SET"
TOKEN_REMOVE = "REMOVE"
TOKEN_ADD = "ADD"
TOKEN_DELETE = "DELETE"
TOKEN_EOF = "EOF"

_ACTION_KEYWORDS = {"SET", "REMOVE", "ADD", "DELETE"}


@dataclass
class Token:
    """A single lexical token."""

    type: str
    value: str
    pos: int


# ---------------------------------------------------------------------------
# Lexer
# ---------------------------------------------------------------------------


def tokenize(expression: str) -> list[Token]:
    """Tokenize a DynamoDB UpdateExpression into a list of tokens."""
    tokens: list[Token] = []
    i = 0
    length = len(expression)

    while i < length:
        ch = expression[i]
        if ch.isspace():
            i += 1
            continue
        i = _scan_next_token(expression, i, length, tokens)

    tokens.append(Token(TOKEN_EOF, "", length))
    return tokens


def _scan_next_token(expression: str, i: int, length: int, tokens: list[Token]) -> int:
    """Scan the next token starting at position i. Returns the new position."""
    ch = expression[i]

    new_i = _scan_single_char(ch, i, tokens)
    if new_i != i:
        return new_i

    if ch == ":":
        return _scan_value_ref(expression, i, tokens)

    if ch == "#":
        return _scan_name_ref(expression, i, tokens)

    if _is_number_start(ch, expression, i, length):
        return _scan_number(expression, i, tokens)

    if ch.isalpha() or ch == "_":
        return _scan_word(expression, i, tokens)

    raise ValueError(f"Unexpected character at position {i}: {ch!r}")


def _is_number_start(ch: str, expression: str, i: int, length: int) -> bool:
    """Check if position i starts a numeric literal."""
    if ch.isdigit():
        return True
    return ch == "-" and i + 1 < length and expression[i + 1].isdigit()


def _scan_single_char(ch: str, i: int, tokens: list[Token]) -> int:
    """Scan single-character tokens like comma, parens, operators."""
    mapping = {
        ",": TOKEN_COMMA,
        "(": TOKEN_LPAREN,
        ")": TOKEN_RPAREN,
        "=": TOKEN_EQUALS,
        "+": TOKEN_PLUS,
        "-": TOKEN_MINUS,
    }
    if ch in mapping:
        tokens.append(Token(mapping[ch], ch, i))
        return i + 1
    return i


def _scan_value_ref(expression: str, i: int, tokens: list[Token]) -> int:
    """Scan a :valueRef token."""
    end = i + 1
    while end < len(expression) and (expression[end].isalnum() or expression[end] == "_"):
        end += 1
    tokens.append(Token(TOKEN_VALUE_REF, expression[i:end], i))
    return end


def _scan_name_ref(expression: str, i: int, tokens: list[Token]) -> int:
    """Scan a #nameRef token."""
    end = i + 1
    while end < len(expression) and (expression[end].isalnum() or expression[end] == "_"):
        end += 1
    tokens.append(Token(TOKEN_NAME_REF, expression[i:end], i))
    return end


def _scan_number(expression: str, i: int, tokens: list[Token]) -> int:
    """Scan a numeric literal."""
    end = i + 1
    has_dot = False
    while end < len(expression) and (expression[end].isdigit() or expression[end] == "."):
        if expression[end] == ".":
            if has_dot:
                break
            has_dot = True
        end += 1
    tokens.append(Token(TOKEN_NUMBER, expression[i:end], i))
    return end


def _scan_word(expression: str, i: int, tokens: list[Token]) -> int:
    """Scan an identifier or keyword."""
    end = i
    while end < len(expression) and (
        expression[end].isalnum() or expression[end] in ("_", ".", "[", "]")
    ):
        end += 1
    word = expression[i:end]
    upper = word.upper()
    if upper in _ACTION_KEYWORDS:
        type_map = {
            "SET": TOKEN_SET,
            "REMOVE": TOKEN_REMOVE,
            "ADD": TOKEN_ADD,
            "DELETE": TOKEN_DELETE,
        }
        tokens.append(Token(type_map[upper], word, i))
    else:
        tokens.append(Token(TOKEN_IDENT, word, i))
    return end


# ---------------------------------------------------------------------------
# AST node types
# ---------------------------------------------------------------------------


@dataclass
class SetAction:
    """SET path = value_expr."""

    path: str
    value_expr: dict


@dataclass
class RemoveAction:
    """REMOVE path."""

    path: str


@dataclass
class AddAction:
    """ADD path value."""

    path: str
    value_expr: dict


@dataclass
class DeleteAction:
    """DELETE path value."""

    path: str
    value_expr: dict


@dataclass
class UpdateActions:
    """Parsed update expression containing all action clauses."""

    set_actions: list[SetAction] = field(default_factory=list)
    remove_actions: list[RemoveAction] = field(default_factory=list)
    add_actions: list[AddAction] = field(default_factory=list)
    delete_actions: list[DeleteAction] = field(default_factory=list)


# ---------------------------------------------------------------------------
# Parser
# ---------------------------------------------------------------------------


class _Parser:
    """Parse a DynamoDB UpdateExpression into UpdateActions."""

    def __init__(self, tokens: list[Token]) -> None:
        self._tokens = tokens
        self._pos = 0

    def _peek(self) -> Token:
        return self._tokens[self._pos]

    def _advance(self) -> Token:
        tok = self._tokens[self._pos]
        self._pos += 1
        return tok

    def _expect(self, token_type: str) -> Token:
        tok = self._advance()
        if tok.type != token_type:
            raise ValueError(
                f"Expected {token_type} at pos {tok.pos}, got {tok.type} ({tok.value!r})"
            )
        return tok

    def parse(self) -> UpdateActions:
        """Parse the full update expression."""
        actions = UpdateActions()
        while self._peek().type != TOKEN_EOF:
            tok = self._peek()
            if tok.type == TOKEN_SET:
                self._advance()
                self._parse_set_clause(actions)
            elif tok.type == TOKEN_REMOVE:
                self._advance()
                self._parse_remove_clause(actions)
            elif tok.type == TOKEN_ADD:
                self._advance()
                self._parse_add_clause(actions)
            elif tok.type == TOKEN_DELETE:
                self._advance()
                self._parse_delete_clause(actions)
            else:
                raise ValueError(f"Expected action keyword at pos {tok.pos}, got {tok.value!r}")
        return actions

    def _parse_set_clause(self, actions: UpdateActions) -> None:
        """Parse one or more SET actions separated by commas."""
        actions.set_actions.append(self._parse_one_set())
        while self._peek().type == TOKEN_COMMA:
            self._advance()
            actions.set_actions.append(self._parse_one_set())

    def _parse_one_set(self) -> SetAction:
        """Parse a single SET action: path = value_expr."""
        path = self._parse_path()
        self._expect(TOKEN_EQUALS)
        value_expr = self._parse_value_expr()
        return SetAction(path=path, value_expr=value_expr)

    def _parse_value_expr(self) -> dict:
        """Parse a value expression for SET clause.

        Handles: plain values, if_not_exists(), list_append(), arithmetic.
        """
        left = self._parse_value_atom()
        return self._parse_arithmetic_tail(left)

    def _parse_arithmetic_tail(self, left: dict) -> dict:
        """Parse optional arithmetic tail (+ or -)."""
        tok = self._peek()
        if tok.type == TOKEN_PLUS:
            self._advance()
            right = self._parse_value_atom()
            return {"op": "add", "left": left, "right": right}
        if tok.type == TOKEN_MINUS:
            self._advance()
            right = self._parse_value_atom()
            return {"op": "subtract", "left": left, "right": right}
        return left

    def _parse_value_atom(self) -> dict:
        """Parse a single value atom: function call, ref, or path."""
        tok = self._peek()

        if tok.type == TOKEN_IDENT and self._is_function():
            return self._parse_function_call()

        if tok.type == TOKEN_VALUE_REF:
            self._advance()
            return {"op": "value_ref", "ref": tok.value}

        if tok.type == TOKEN_NAME_REF:
            self._advance()
            return {"op": "name_ref", "ref": tok.value}

        if tok.type == TOKEN_IDENT:
            self._advance()
            return {"op": "path", "path": tok.value}

        if tok.type == TOKEN_NUMBER:
            self._advance()
            return {"op": "literal", "value": _parse_number(tok.value)}

        raise ValueError(f"Unexpected token at pos {tok.pos}: {tok.value!r}")

    def _is_function(self) -> bool:
        """Check if current IDENT is followed by LPAREN (function call)."""
        next_pos = self._pos + 1
        if next_pos < len(self._tokens):
            return self._tokens[next_pos].type == TOKEN_LPAREN
        return False

    def _parse_function_call(self) -> dict:
        """Parse a function call like if_not_exists(path, value) or list_append(a, b)."""
        name_tok = self._advance()
        self._expect(TOKEN_LPAREN)
        args: list[dict] = []
        if self._peek().type != TOKEN_RPAREN:
            args.append(self._parse_value_expr())
            while self._peek().type == TOKEN_COMMA:
                self._advance()
                args.append(self._parse_value_expr())
        self._expect(TOKEN_RPAREN)
        return {"op": "function", "name": name_tok.value.lower(), "args": args}

    def _parse_remove_clause(self, actions: UpdateActions) -> None:
        """Parse one or more REMOVE actions separated by commas."""
        actions.remove_actions.append(RemoveAction(path=self._parse_path()))
        while self._peek().type == TOKEN_COMMA:
            self._advance()
            actions.remove_actions.append(RemoveAction(path=self._parse_path()))

    def _parse_add_clause(self, actions: UpdateActions) -> None:
        """Parse one or more ADD actions: path value."""
        action = self._parse_one_add()
        actions.add_actions.append(action)
        while self._peek().type == TOKEN_COMMA:
            self._advance()
            actions.add_actions.append(self._parse_one_add())

    def _parse_one_add(self) -> AddAction:
        """Parse a single ADD action: path value."""
        path = self._parse_path()
        value_expr = self._parse_value_atom()
        return AddAction(path=path, value_expr=value_expr)

    def _parse_delete_clause(self, actions: UpdateActions) -> None:
        """Parse one or more DELETE actions: path value."""
        action = self._parse_one_delete()
        actions.delete_actions.append(action)
        while self._peek().type == TOKEN_COMMA:
            self._advance()
            actions.delete_actions.append(self._parse_one_delete())

    def _parse_one_delete(self) -> DeleteAction:
        """Parse a single DELETE action: path value."""
        path = self._parse_path()
        value_expr = self._parse_value_atom()
        return DeleteAction(path=path, value_expr=value_expr)

    def _parse_path(self) -> str:
        """Parse an attribute path (possibly with #name refs)."""
        tok = self._peek()
        if tok.type == TOKEN_NAME_REF:
            self._advance()
            return tok.value
        if tok.type == TOKEN_IDENT:
            self._advance()
            return tok.value
        raise ValueError(f"Expected path at pos {tok.pos}, got {tok.value!r}")


def _parse_number(s: str) -> int | float:
    """Parse a numeric string to int or float."""
    return int(s) if "." not in s else float(s)


# ---------------------------------------------------------------------------
# Evaluator
# ---------------------------------------------------------------------------


def _unwrap_dynamo_value(raw: Any) -> Any:
    """Unwrap a DynamoDB typed value to a plain Python value."""
    if raw is None:
        return None
    if not isinstance(raw, dict):
        return raw
    type_converters = {
        "S": lambda v: v,
        "N": lambda v: int(v) if "." not in str(v) else float(v),
        "B": lambda v: v,
        "BOOL": lambda v: v,
        "NULL": lambda v: None,
        "L": lambda v: [_unwrap_dynamo_value(item) for item in v],
        "M": lambda v: {k: _unwrap_dynamo_value(val) for k, val in v.items()},
        "SS": lambda v: set(v),
        "NS": lambda v: {int(n) if "." not in str(n) else float(n) for n in v},
        "BS": lambda v: set(v),
    }
    for type_key, converter in type_converters.items():
        if type_key in raw:
            return converter(raw[type_key])
    return raw


def _resolve_path(item: dict, path: str) -> tuple[bool, Any]:
    """Resolve a dotted path against an item. Returns (found, value)."""
    parts = path.split(".")
    current: Any = item
    for part in parts:
        if isinstance(current, dict) and part in current:
            current = current[part]
        else:
            return False, None
    return True, current


def _set_path(item: dict, path: str, value: Any) -> None:
    """Set a value at a dotted path in item, creating intermediate dicts as needed."""
    parts = path.split(".")
    current = item
    for part in parts[:-1]:
        if part not in current or not isinstance(current[part], dict):
            current[part] = {}
        current = current[part]
    current[parts[-1]] = value


def _remove_path(item: dict, path: str) -> None:
    """Remove a value at a dotted path in item."""
    parts = path.split(".")
    current = item
    for part in parts[:-1]:
        if isinstance(current, dict) and part in current:
            current = current[part]
        else:
            return
    if isinstance(current, dict):
        current.pop(parts[-1], None)


class UpdateExpressionEvaluator:
    """Evaluate a parsed UpdateExpression against an item.

    Parameters
    ----------
    expression_names : dict | None
        Mapping of ``#name`` placeholders to real attribute names.
    expression_values : dict | None
        Mapping of ``:value`` placeholders to DynamoDB-typed values.
    """

    def __init__(
        self,
        expression_names: dict[str, str] | None = None,
        expression_values: dict[str, Any] | None = None,
    ) -> None:
        self._names = expression_names or {}
        self._values = expression_values or {}

    def apply(self, actions: UpdateActions, item: dict) -> dict:
        """Apply all update actions to item (mutates in-place). Returns the item."""
        for action in actions.set_actions:
            self._apply_set(action, item)
        for action in actions.remove_actions:
            self._apply_remove(action, item)
        for action in actions.add_actions:
            self._apply_add(action, item)
        for action in actions.delete_actions:
            self._apply_delete(action, item)
        return item

    def _resolve_name(self, token: str) -> str:
        """Resolve a #name ref or return the token as-is."""
        if token.startswith("#"):
            return self._names.get(token, token)
        return token

    def _eval_value_expr(self, expr: dict, item: dict) -> Any:
        """Evaluate a value expression to a concrete Python value."""
        op = expr["op"]
        dispatch = {
            "value_ref": self._eval_value_ref,
            "name_ref": self._eval_name_ref,
            "path": self._eval_path,
            "literal": self._eval_literal,
            "function": self._eval_function,
            "add": self._eval_add,
            "subtract": self._eval_subtract,
        }
        handler = dispatch.get(op)
        if handler is None:
            raise ValueError(f"Unknown value expression op: {op}")
        return handler(expr, item)

    def _eval_value_ref(self, expr: dict, item: dict) -> Any:
        raw = self._values.get(expr["ref"])
        return _unwrap_dynamo_value(raw)

    def _eval_name_ref(self, expr: dict, item: dict) -> Any:
        real_name = self._names.get(expr["ref"], expr["ref"])
        found, val = _resolve_path(item, real_name)
        return val if found else None

    def _eval_path(self, expr: dict, item: dict) -> Any:
        found, val = _resolve_path(item, expr["path"])
        return val if found else None

    def _eval_literal(self, expr: dict, item: dict) -> Any:
        return expr["value"]

    def _eval_function(self, expr: dict, item: dict) -> Any:
        name = expr["name"]
        if name == "if_not_exists":
            return self._func_if_not_exists(expr["args"], item)
        if name == "list_append":
            return self._func_list_append(expr["args"], item)
        raise ValueError(f"Unknown function in SET: {name}")

    def _eval_add(self, expr: dict, item: dict) -> Any:
        left = self._eval_value_expr(expr["left"], item)
        right = self._eval_value_expr(expr["right"], item)
        return _numeric_op(left, right, operator="add")

    def _eval_subtract(self, expr: dict, item: dict) -> Any:
        left = self._eval_value_expr(expr["left"], item)
        right = self._eval_value_expr(expr["right"], item)
        return _numeric_op(left, right, operator="subtract")

    def _func_if_not_exists(self, args: list[dict], item: dict) -> Any:
        """if_not_exists(path, value): return current value if exists, else default."""
        path_expr = args[0]
        default_expr = args[1]
        path = self._resolve_path_from_expr(path_expr)
        found, val = _resolve_path(item, path)
        if found:
            return val
        return self._eval_value_expr(default_expr, item)

    def _func_list_append(self, args: list[dict], item: dict) -> list:
        """list_append(list1, list2): concatenate two lists."""
        list1 = self._eval_value_expr(args[0], item)
        list2 = self._eval_value_expr(args[1], item)
        result1 = list1 if isinstance(list1, list) else [list1] if list1 is not None else []
        result2 = list2 if isinstance(list2, list) else [list2] if list2 is not None else []
        return result1 + result2

    def _resolve_path_from_expr(self, expr: dict) -> str:
        """Resolve an expression node to an attribute path string."""
        if expr["op"] == "name_ref":
            return self._names.get(expr["ref"], expr["ref"])
        if expr["op"] == "path":
            return expr["path"]
        return str(expr.get("ref", expr.get("path", "")))

    def _apply_set(self, action: SetAction, item: dict) -> None:
        """Apply a single SET action."""
        path = self._resolve_name(action.path)
        value = self._eval_value_expr(action.value_expr, item)
        _set_path(item, path, value)

    def _apply_remove(self, action: RemoveAction, item: dict) -> None:
        """Apply a single REMOVE action."""
        path = self._resolve_name(action.path)
        _remove_path(item, path)

    def _apply_add(self, action: AddAction, item: dict) -> None:
        """Apply a single ADD action: add to number or set."""
        path = self._resolve_name(action.path)
        value = self._eval_value_expr(action.value_expr, item)
        found, current = _resolve_path(item, path)
        if not found:
            _set_path(item, path, value)
            return
        new_val = _add_values(current, value)
        _set_path(item, path, new_val)

    def _apply_delete(self, action: DeleteAction, item: dict) -> None:
        """Apply a single DELETE action: remove elements from a set."""
        path = self._resolve_name(action.path)
        value = self._eval_value_expr(action.value_expr, item)
        found, current = _resolve_path(item, path)
        if not found or not isinstance(current, set):
            return
        if isinstance(value, set):
            _set_path(item, path, current - value)
        else:
            current.discard(value)
            _set_path(item, path, current)


def _numeric_op(left: Any, right: Any, operator: str) -> Any:
    """Perform arithmetic on two values."""
    left_num = _to_number(left)
    right_num = _to_number(right)
    if operator == "add":
        return left_num + right_num
    return left_num - right_num


def _to_number(val: Any) -> int | float:
    """Convert a value to a number."""
    if isinstance(val, (int, float)):
        return val
    if isinstance(val, str):
        return int(val) if "." not in val else float(val)
    return 0


def _add_values(current: Any, value: Any) -> Any:
    """ADD operation: add to number or union with set."""
    if isinstance(current, (int, float)) and isinstance(value, (int, float)):
        return current + value
    if isinstance(current, set):
        if isinstance(value, set):
            return current | value
        return current | {value}
    if isinstance(current, (int, float)):
        return current + _to_number(value)
    return value


# ---------------------------------------------------------------------------
# Public API
# ---------------------------------------------------------------------------


def parse_update_expression(expression: str) -> UpdateActions:
    """Parse a DynamoDB UpdateExpression string into UpdateActions."""
    tokens = tokenize(expression)
    parser = _Parser(tokens)
    return parser.parse()


def apply_update_expression(
    item: dict,
    expression: str,
    expression_names: dict[str, str] | None = None,
    expression_values: dict[str, Any] | None = None,
) -> dict:
    """Apply a DynamoDB UpdateExpression to an item (mutates in-place).

    Parameters
    ----------
    item : dict
        The item to update.
    expression : str
        The DynamoDB UpdateExpression string.
    expression_names : dict | None
        Mapping of #name placeholders to real attribute names.
    expression_values : dict | None
        Mapping of :value placeholders to DynamoDB-typed values.

    Returns
    -------
    dict
        The updated item.
    """
    actions = parse_update_expression(expression)
    evaluator = UpdateExpressionEvaluator(expression_names, expression_values)
    return evaluator.apply(actions, item)

"""Recursive-descent FilterExpression evaluator for DynamoDB expressions.

Supports:
- Comparison operators: =, <>, <, >, <=, >=
- Logical operators: AND, OR, NOT (with proper precedence)
- BETWEEN, IN operators
- Functions: attribute_exists(), attribute_not_exists(), begins_with(), contains(), size()
- ExpressionAttributeNames (#attr) and ExpressionAttributeValues (:val) resolution
"""

from __future__ import annotations

import re
from dataclasses import dataclass
from typing import Any

# ---------------------------------------------------------------------------
# Token types
# ---------------------------------------------------------------------------

TOKEN_IDENT = "IDENT"
TOKEN_VALUE_REF = "VALUE_REF"
TOKEN_NAME_REF = "NAME_REF"
TOKEN_NUMBER = "NUMBER"
TOKEN_STRING = "STRING"
TOKEN_COMMA = "COMMA"
TOKEN_LPAREN = "LPAREN"
TOKEN_RPAREN = "RPAREN"
TOKEN_OP = "OP"
TOKEN_AND = "AND"
TOKEN_OR = "OR"
TOKEN_NOT = "NOT"
TOKEN_BETWEEN = "BETWEEN"
TOKEN_IN = "IN"
TOKEN_EOF = "EOF"

_KEYWORDS = {"AND", "OR", "NOT", "BETWEEN", "IN"}

_OPERATOR_RE = re.compile(r"<>|<=|>=|[=<>]")


@dataclass
class Token:
    """A single lexical token."""

    type: str
    value: str
    pos: int


# ---------------------------------------------------------------------------
# Lexer
# ---------------------------------------------------------------------------


def tokenize(expression: str) -> list[Token]:
    """Tokenize a DynamoDB filter expression into a list of tokens."""
    tokens: list[Token] = []
    i = 0
    length = len(expression)

    while i < length:
        ch = expression[i]

        if ch.isspace():
            i += 1
            continue

        if ch == ",":
            tokens.append(Token(TOKEN_COMMA, ",", i))
            i += 1
            continue

        if ch == "(":
            tokens.append(Token(TOKEN_LPAREN, "(", i))
            i += 1
            continue

        if ch == ")":
            tokens.append(Token(TOKEN_RPAREN, ")", i))
            i += 1
            continue

        token, i = _try_operator(expression, i, tokens)
        if token:
            continue

        token, i = _try_value_ref(expression, i, tokens)
        if token:
            continue

        token, i = _try_name_ref(expression, i, tokens)
        if token:
            continue

        token, i = _try_number_literal(expression, i, tokens)
        if token:
            continue

        # Identifier or keyword
        i = _scan_identifier(expression, i, tokens)

    tokens.append(Token(TOKEN_EOF, "", length))
    return tokens


def _try_operator(expression: str, i: int, tokens: list[Token]) -> tuple[Token | None, int]:
    """Try to scan a comparison operator at position i."""
    m = _OPERATOR_RE.match(expression, i)
    if m:
        tok = Token(TOKEN_OP, m.group(), i)
        tokens.append(tok)
        return tok, m.end()
    return None, i


def _try_value_ref(expression: str, i: int, tokens: list[Token]) -> tuple[Token | None, int]:
    """Try to scan a :valueRef at position i."""
    if expression[i] == ":":
        end = i + 1
        while end < len(expression) and (expression[end].isalnum() or expression[end] == "_"):
            end += 1
        tok = Token(TOKEN_VALUE_REF, expression[i:end], i)
        tokens.append(tok)
        return tok, end
    return None, i


def _try_name_ref(expression: str, i: int, tokens: list[Token]) -> tuple[Token | None, int]:
    """Try to scan a #nameRef at position i."""
    if expression[i] == "#":
        end = i + 1
        while end < len(expression) and (expression[end].isalnum() or expression[end] == "_"):
            end += 1
        tok = Token(TOKEN_NAME_REF, expression[i:end], i)
        tokens.append(tok)
        return tok, end
    return None, i


def _try_number_literal(expression: str, i: int, tokens: list[Token]) -> tuple[Token | None, int]:
    """Try to scan a numeric literal at position i."""
    if expression[i].isdigit() or (
        expression[i] == "-" and i + 1 < len(expression) and expression[i + 1].isdigit()
    ):
        end = i + 1
        has_dot = False
        while end < len(expression) and (expression[end].isdigit() or expression[end] == "."):
            if expression[end] == ".":
                if has_dot:
                    break
                has_dot = True
            end += 1
        tok = Token(TOKEN_NUMBER, expression[i:end], i)
        tokens.append(tok)
        return tok, end
    return None, i


def _scan_identifier(expression: str, i: int, tokens: list[Token]) -> int:
    """Scan an identifier or keyword at position i."""
    end = i
    while end < len(expression) and (expression[end].isalnum() or expression[end] in ("_", ".")):
        end += 1
    if end == i:
        raise ValueError(f"Unexpected character at position {i}: {expression[i]!r}")
    word = expression[i:end]
    upper = word.upper()
    if upper in _KEYWORDS:
        type_map = {
            "AND": TOKEN_AND,
            "OR": TOKEN_OR,
            "NOT": TOKEN_NOT,
            "BETWEEN": TOKEN_BETWEEN,
            "IN": TOKEN_IN,
        }
        tokens.append(Token(type_map[upper], word, i))
    else:
        tokens.append(Token(TOKEN_IDENT, word, i))
    return end


# ---------------------------------------------------------------------------
# Parser (recursive descent)
# ---------------------------------------------------------------------------


class _Parser:
    """Recursive descent parser for DynamoDB filter expressions.

    Grammar (simplified):
        expr       -> or_expr
        or_expr    -> and_expr (OR and_expr)*
        and_expr   -> not_expr (AND not_expr)*
        not_expr   -> NOT not_expr | comparison
        comparison -> operand (comp_op operand | BETWEEN operand AND operand | IN (list))?
        operand    -> function_call | atom
        atom       -> VALUE_REF | NAME_REF | IDENT | NUMBER | '(' expr ')'
    """

    def __init__(self, tokens: list[Token]) -> None:
        self._tokens = tokens
        self._pos = 0

    def _peek(self) -> Token:
        return self._tokens[self._pos]

    def _advance(self) -> Token:
        tok = self._tokens[self._pos]
        self._pos += 1
        return tok

    def _expect(self, token_type: str) -> Token:
        tok = self._advance()
        if tok.type != token_type:
            raise ValueError(
                f"Expected {token_type} at pos {tok.pos}, got {tok.type} ({tok.value!r})"
            )
        return tok

    def parse(self) -> dict:
        """Parse the full expression and return an AST dict."""
        result = self._or_expr()
        if self._peek().type != TOKEN_EOF:
            tok = self._peek()
            raise ValueError(f"Unexpected token at pos {tok.pos}: {tok.value!r}")
        return result

    def _or_expr(self) -> dict:
        left = self._and_expr()
        while self._peek().type == TOKEN_OR:
            self._advance()
            right = self._and_expr()
            left = {"op": "OR", "left": left, "right": right}
        return left

    def _and_expr(self) -> dict:
        left = self._not_expr()
        while self._peek().type == TOKEN_AND:
            self._advance()
            right = self._not_expr()
            left = {"op": "AND", "left": left, "right": right}
        return left

    def _not_expr(self) -> dict:
        if self._peek().type == TOKEN_NOT:
            self._advance()
            operand = self._not_expr()
            return {"op": "NOT", "operand": operand}
        return self._comparison()

    def _comparison(self) -> dict:
        left = self._operand()
        return self._comparison_tail(left)

    def _comparison_tail(self, left: dict) -> dict:
        """Parse the optional tail of a comparison (operator, BETWEEN, IN)."""
        tok = self._peek()

        if tok.type == TOKEN_OP:
            self._advance()
            right = self._operand()
            return {"op": "compare", "comparator": tok.value, "left": left, "right": right}

        if tok.type == TOKEN_BETWEEN:
            return self._parse_between(left)

        if tok.type == TOKEN_IN:
            return self._parse_in(left)

        return left

    def _parse_between(self, left: dict) -> dict:
        """Parse BETWEEN low AND high."""
        self._advance()  # consume BETWEEN
        low = self._operand()
        self._expect(TOKEN_AND)
        high = self._operand()
        return {"op": "BETWEEN", "operand": left, "low": low, "high": high}

    def _parse_in(self, left: dict) -> dict:
        """Parse IN (val1, val2, ...)."""
        self._advance()  # consume IN
        self._expect(TOKEN_LPAREN)
        values: list[dict] = []
        if self._peek().type != TOKEN_RPAREN:
            values.append(self._operand())
            while self._peek().type == TOKEN_COMMA:
                self._advance()
                values.append(self._operand())
        self._expect(TOKEN_RPAREN)
        return {"op": "IN", "operand": left, "values": values}

    def _operand(self) -> dict:
        """Parse an operand: function call, atom, or parenthesized expr."""
        tok = self._peek()

        if tok.type == TOKEN_IDENT and self._is_function_name(tok.value):
            return self._function_call()

        if tok.type == TOKEN_LPAREN:
            self._advance()
            inner = self._or_expr()
            self._expect(TOKEN_RPAREN)
            return inner

        return self._atom()

    def _is_function_name(self, name: str) -> bool:
        """Check if the identifier is a known function and followed by '('."""
        known = {"attribute_exists", "attribute_not_exists", "begins_with", "contains", "size"}
        if name.lower() not in known:
            return False
        # Look ahead for LPAREN
        next_pos = self._pos + 1
        if next_pos < len(self._tokens):
            return self._tokens[next_pos].type == TOKEN_LPAREN
        return False

    def _function_call(self) -> dict:
        """Parse a function call: func_name(arg1, arg2, ...)."""
        name_tok = self._advance()
        self._expect(TOKEN_LPAREN)
        args: list[dict] = []
        if self._peek().type != TOKEN_RPAREN:
            args.append(self._or_expr())
            while self._peek().type == TOKEN_COMMA:
                self._advance()
                args.append(self._or_expr())
        self._expect(TOKEN_RPAREN)
        return {"op": "function", "name": name_tok.value.lower(), "args": args}

    def _atom(self) -> dict:
        """Parse an atomic value: value ref, name ref, identifier, or number."""
        tok = self._advance()
        if tok.type == TOKEN_VALUE_REF:
            return {"op": "value_ref", "ref": tok.value}
        if tok.type == TOKEN_NAME_REF:
            return {"op": "name_ref", "ref": tok.value}
        if tok.type == TOKEN_IDENT:
            return {"op": "path", "path": tok.value}
        if tok.type == TOKEN_NUMBER:
            return {"op": "literal", "value": _parse_number(tok.value)}
        raise ValueError(f"Unexpected token at pos {tok.pos}: {tok.type} ({tok.value!r})")


def _parse_number(s: str) -> int | float:
    """Parse a numeric string to int or float."""
    return int(s) if "." not in s else float(s)


# ---------------------------------------------------------------------------
# AST Evaluator
# ---------------------------------------------------------------------------


def _resolve_path(item: dict, path: str) -> tuple[bool, Any]:
    """Resolve a dotted attribute path against an item. Returns (found, value)."""
    parts = path.split(".")
    current: Any = item
    for part in parts:
        if isinstance(current, dict) and part in current:
            current = current[part]
        else:
            return False, None
    return True, current


class ExpressionEvaluator:
    """Evaluates a parsed DynamoDB filter expression AST against an item.

    Parameters
    ----------
    expression_names : dict | None
        Mapping of ``#name`` placeholders to real attribute names.
    expression_values : dict | None
        Mapping of ``:value`` placeholders to DynamoDB-typed values.
    """

    def __init__(
        self,
        expression_names: dict[str, str] | None = None,
        expression_values: dict[str, Any] | None = None,
    ) -> None:
        self._names = expression_names or {}
        self._values = expression_values or {}

    def evaluate(self, ast: dict, item: dict) -> bool:
        """Evaluate the AST against a single item. Returns True if the item matches."""
        result = self._eval_node(ast, item)
        return bool(result)

    def _eval_node(self, node: dict, item: dict) -> Any:
        """Dispatch evaluation based on node op type."""
        op = node["op"]
        dispatch = {
            "AND": self._eval_and,
            "OR": self._eval_or,
            "NOT": self._eval_not,
            "compare": self._eval_compare,
            "BETWEEN": self._eval_between,
            "IN": self._eval_in,
            "function": self._eval_function,
            "value_ref": self._eval_value_ref,
            "name_ref": self._eval_name_ref,
            "path": self._eval_path,
            "literal": self._eval_literal,
        }
        handler = dispatch.get(op)
        if handler is None:
            raise ValueError(f"Unknown AST node op: {op}")
        return handler(node, item)

    def _eval_and(self, node: dict, item: dict) -> bool:
        return bool(self._eval_node(node["left"], item) and self._eval_node(node["right"], item))

    def _eval_or(self, node: dict, item: dict) -> bool:
        return bool(self._eval_node(node["left"], item) or self._eval_node(node["right"], item))

    def _eval_not(self, node: dict, item: dict) -> bool:
        return not self._eval_node(node["operand"], item)

    def _eval_compare(self, node: dict, item: dict) -> bool:
        left = self._eval_node(node["left"], item)
        right = self._eval_node(node["right"], item)
        if left is None or right is None:
            return False
        left, right = _coerce_for_comparison(left, right)
        return _apply_comparator(node["comparator"], left, right)

    def _eval_between(self, node: dict, item: dict) -> bool:
        val = self._eval_node(node["operand"], item)
        low = self._eval_node(node["low"], item)
        high = self._eval_node(node["high"], item)
        if val is None or low is None or high is None:
            return False
        val, low = _coerce_for_comparison(val, low)
        val, high = _coerce_for_comparison(val, high)
        return low <= val <= high

    def _eval_in(self, node: dict, item: dict) -> bool:
        val = self._eval_node(node["operand"], item)
        if val is None:
            return False
        for v_node in node["values"]:
            candidate = self._eval_node(v_node, item)
            coerced_val, coerced_candidate = _coerce_for_comparison(val, candidate)
            if coerced_val == coerced_candidate:
                return True
        return False

    def _eval_function(self, node: dict, item: dict) -> Any:
        name = node["name"]
        func_map = {
            "attribute_exists": self._func_attribute_exists,
            "attribute_not_exists": self._func_attribute_not_exists,
            "begins_with": self._func_begins_with,
            "contains": self._func_contains,
            "size": self._func_size,
        }
        func = func_map.get(name)
        if func is None:
            raise ValueError(f"Unknown function: {name}")
        return func(node["args"], item)

    def _eval_value_ref(self, node: dict, item: dict) -> Any:
        ref = node["ref"]
        raw = self._values.get(ref)
        return _unwrap_dynamo_value(raw)

    def _eval_name_ref(self, node: dict, item: dict) -> Any:
        ref = node["ref"]
        real_name = self._names.get(ref, ref)
        found, val = _resolve_path(item, real_name)
        return val if found else None

    def _eval_path(self, node: dict, item: dict) -> Any:
        found, val = _resolve_path(item, node["path"])
        return val if found else None

    def _eval_literal(self, node: dict, item: dict) -> Any:
        return node["value"]

    # -- Built-in functions ---

    def _func_attribute_exists(self, args: list[dict], item: dict) -> bool:
        path = self._resolve_attr_path(args[0])
        found, _ = _resolve_path(item, path)
        return found

    def _func_attribute_not_exists(self, args: list[dict], item: dict) -> bool:
        path = self._resolve_attr_path(args[0])
        found, _ = _resolve_path(item, path)
        return not found

    def _func_begins_with(self, args: list[dict], item: dict) -> bool:
        val = self._eval_node(args[0], item)
        prefix = self._eval_node(args[1], item)
        if not isinstance(val, str) or not isinstance(prefix, str):
            return False
        return val.startswith(prefix)

    def _func_contains(self, args: list[dict], item: dict) -> bool:
        val = self._eval_node(args[0], item)
        operand = self._eval_node(args[1], item)
        if isinstance(val, str) and isinstance(operand, str):
            return operand in val
        if isinstance(val, (list, set)):
            return operand in val
        return False

    def _func_size(self, args: list[dict], item: dict) -> int:
        val = self._eval_node(args[0], item)
        if val is None:
            return 0
        if isinstance(val, (str, list, dict, set)):
            return len(val)
        return 0

    def _resolve_attr_path(self, node: dict) -> str:
        """Resolve an AST node to an attribute path string."""
        if node["op"] == "name_ref":
            return self._names.get(node["ref"], node["ref"])
        if node["op"] == "path":
            return node["path"]
        return str(node.get("ref", node.get("path", "")))


# ---------------------------------------------------------------------------
# Helper functions
# ---------------------------------------------------------------------------


def _unwrap_dynamo_value(raw: Any) -> Any:
    """Unwrap a DynamoDB typed value to a plain Python value."""
    if raw is None:
        return None
    if not isinstance(raw, dict):
        return raw
    type_converters = {
        "S": lambda v: v,
        "N": lambda v: int(v) if "." not in str(v) else float(v),
        "B": lambda v: v,
        "BOOL": lambda v: v,
        "NULL": lambda v: None,
        "L": lambda v: [_unwrap_dynamo_value(item) for item in v],
        "M": lambda v: {k: _unwrap_dynamo_value(val) for k, val in v.items()},
        "SS": lambda v: set(v),
        "NS": lambda v: {int(n) if "." not in str(n) else float(n) for n in v},
        "BS": lambda v: set(v),
    }
    for type_key, converter in type_converters.items():
        if type_key in raw:
            return converter(raw[type_key])
    return raw


def _coerce_for_comparison(a: Any, b: Any) -> tuple[Any, Any]:
    """Coerce two values to comparable types."""
    if isinstance(a, str) and isinstance(b, (int, float)):
        try:
            a = type(b)(a)
        except (ValueError, TypeError):
            pass
    if isinstance(b, str) and isinstance(a, (int, float)):
        try:
            b = type(a)(b)
        except (ValueError, TypeError):
            pass
    return a, b


_COMPARATORS: dict[str, Any] = {
    "=": lambda a, b: a == b,
    "<>": lambda a, b: a != b,
    "<": lambda a, b: a < b,
    ">": lambda a, b: a > b,
    "<=": lambda a, b: a <= b,
    ">=": lambda a, b: a >= b,
}


def _apply_comparator(op: str, left: Any, right: Any) -> bool:
    """Apply a comparison operator to two values."""
    cmp_fn = _COMPARATORS.get(op)
    if cmp_fn is None:
        raise ValueError(f"Unknown comparator: {op}")
    try:
        return bool(cmp_fn(left, right))
    except TypeError:
        return False


# ---------------------------------------------------------------------------
# Public API
# ---------------------------------------------------------------------------


def parse_filter_expression(expression: str) -> dict:
    """Parse a DynamoDB FilterExpression string into an AST.

    Returns a dict representing the abstract syntax tree.
    """
    tokens = tokenize(expression)
    parser = _Parser(tokens)
    return parser.parse()


def evaluate_filter_expression(
    item: dict,
    expression: str,
    expression_names: dict[str, str] | None = None,
    expression_values: dict[str, Any] | None = None,
) -> bool:
    """Evaluate a DynamoDB FilterExpression against a single item.

    Parameters
    ----------
    item : dict
        The item to evaluate (plain Python dict, not DynamoDB JSON).
    expression : str
        The DynamoDB FilterExpression string.
    expression_names : dict | None
        Mapping of #name placeholders to real attribute names.
    expression_values : dict | None
        Mapping of :value placeholders to DynamoDB-typed values.

    Returns
    -------
    bool
        True if the item matches the expression.
    """
    ast = parse_filter_expression(expression)
    evaluator = ExpressionEvaluator(expression_names, expression_values)
    return evaluator.evaluate(ast, item)


def apply_filter_expression(
    items: list[dict],
    expression: str | None,
    expression_names: dict[str, str] | None = None,
    expression_values: dict[str, Any] | None = None,
) -> list[dict]:
    """Filter a list of items using a DynamoDB FilterExpression.

    If expression is None or empty, returns all items unchanged.
    """
    if not expression:
        return items
    ast = parse_filter_expression(expression)
    evaluator = ExpressionEvaluator(expression_names, expression_values)
    return [item for item in items if evaluator.evaluate(ast, item)]

"""EventBridge event pattern matching engine.

Implements the EventBridge pattern matching logic as pure functions.
Supports: exact value matching, prefix matching, numeric range,
exists/not-exists, anything-but, and nested detail patterns.
"""

from __future__ import annotations


def match_event(pattern: dict, event: dict) -> bool:
    """Return True if *event* matches the EventBridge *pattern*.

    Each key in the pattern must be present in the event and at least one
    of the pattern conditions for that key must match the event value.

    Parameters
    ----------
    pattern:
        An EventBridge event pattern dict. Keys map to lists of match
        conditions.
    event:
        The event dict to test against the pattern.

    Returns
    -------
    bool
        True if the event matches all conditions in the pattern.
    """
    if not pattern:
        return True

    for key, conditions in pattern.items():
        event_value = event.get(key)
        if not _key_matches(conditions, event_value):
            return False

    return True


def _key_matches(conditions: list | dict, event_value: object) -> bool:
    """Check whether a single pattern key matches the event value.

    If *conditions* is a dict, it represents a nested pattern that must
    be matched recursively against the event value (which should also be
    a dict).  If it is a list, each element is a match condition and at
    least one must match.
    """
    if isinstance(conditions, dict):
        if not isinstance(event_value, dict):
            return False
        return match_event(conditions, event_value)

    if not isinstance(conditions, list):
        return False

    return _any_condition_matches(conditions, event_value)


def _any_condition_matches(conditions: list, event_value: object) -> bool:
    """Return True if at least one condition in the list matches."""
    for condition in conditions:
        if _single_condition_matches(condition, event_value):
            return True
    return False


def _single_condition_matches(condition: object, event_value: object) -> bool:
    """Evaluate a single match condition against an event value."""
    if isinstance(condition, dict):
        return _dict_condition_matches(condition, event_value)

    # Exact value match (string, number, boolean)
    return event_value == condition


def _dict_condition_matches(condition: dict, event_value: object) -> bool:
    """Dispatch structured condition objects."""
    if "exists" in condition:
        return _eval_exists(event_value, condition["exists"])

    if "prefix" in condition:
        return _eval_prefix(event_value, condition["prefix"])

    if "numeric" in condition:
        return _eval_numeric(event_value, condition["numeric"])

    if "anything-but" in condition:
        return _eval_anything_but(event_value, condition["anything-but"])

    return False


# ------------------------------------------------------------------
# Individual evaluators
# ------------------------------------------------------------------


def _eval_exists(event_value: object, should_exist: bool) -> bool:
    """Evaluate an ``{"exists": true/false}`` condition."""
    if should_exist:
        return event_value is not None
    return event_value is None


def _eval_prefix(event_value: object, prefix: str) -> bool:
    """Evaluate a ``{"prefix": "val"}`` condition."""
    if not isinstance(event_value, str):
        return False
    return event_value.startswith(prefix)


def _eval_numeric(event_value: object, operators: list) -> bool:
    """Evaluate a ``{"numeric": [">=", 100, "<", 200]}`` condition.

    The *operators* list contains alternating (operator, operand) pairs.
    All pairs must match.
    """
    try:
        num_value = float(event_value)  # type: ignore[arg-type]
    except (TypeError, ValueError):
        return False

    i = 0
    while i < len(operators) - 1:
        op = operators[i]
        operand = float(operators[i + 1])
        if not _compare_numeric(num_value, op, operand):
            return False
        i += 2

    return True


def _compare_numeric(value: float, op: str, operand: float) -> bool:
    """Perform a single numeric comparison."""
    if op == "=":
        return value == operand
    if op == ">":
        return value > operand
    if op == ">=":
        return value >= operand
    if op == "<":
        return value < operand
    if op == "<=":
        return value <= operand
    return False


def _eval_anything_but(event_value: object, exclusions: object) -> bool:
    """Evaluate an ``{"anything-but": ["red"]}`` or ``{"anything-but": "red"}`` condition.

    Returns True when *event_value* does NOT match any of the exclusions.
    """
    if event_value is None:
        return False

    if isinstance(exclusions, list):
        return all(event_value != exc for exc in exclusions)

    # Single value exclusion
    return event_value != exclusions

"""SNS message filter policy evaluation.

Implements the subset of SNS subscription filter policies used by
local development: exact string matching, numeric comparisons,
exists checks, and anything-but exclusion.
"""

from __future__ import annotations


def matches_filter_policy(
    message_attributes: dict,
    filter_policy: dict | None,
) -> bool:
    """Return True if *message_attributes* satisfy *filter_policy*.

    When *filter_policy* is ``None`` or empty every message matches.
    Each key in the policy must be present in *message_attributes* and
    at least one of the policy conditions for that key must match.

    Supported condition types:
    - Exact string: ``{"color": ["red", "blue"]}``
    - Numeric:      ``{"price": [{"numeric": [">=", 100]}]}``
    - Exists:       ``{"color": [{"exists": true}]}``
    - Anything-but: ``{"color": [{"anything-but": ["red"]}]}``
    """
    if not filter_policy:
        return True

    for key, conditions in filter_policy.items():
        attr = message_attributes.get(key)
        if not _any_condition_matches(attr, conditions):
            return False

    return True


def _any_condition_matches(attr: dict | None, conditions: list) -> bool:
    """Return True if at least one condition in the list matches *attr*."""
    for condition in conditions:
        if _single_condition_matches(attr, condition):
            return True
    return False


def _single_condition_matches(attr: dict | None, condition: object) -> bool:
    """Evaluate a single filter condition against an attribute value."""
    if isinstance(condition, dict):
        return _dict_condition_matches(attr, condition)

    # Exact string match -- condition is a plain string
    if attr is None:
        return False
    attr_value = _extract_value(attr)
    return str(attr_value) == str(condition)


def _dict_condition_matches(attr: dict | None, condition: dict) -> bool:
    """Dispatch structured condition objects (exists, numeric, anything-but)."""
    if "exists" in condition:
        return _eval_exists(attr, condition["exists"])

    if "numeric" in condition:
        if attr is None:
            return False
        return _eval_numeric(_extract_value(attr), condition["numeric"])

    if "anything-but" in condition:
        if attr is None:
            return False
        return _eval_anything_but(_extract_value(attr), condition["anything-but"])

    return False


# ------------------------------------------------------------------
# Individual evaluators
# ------------------------------------------------------------------


def _eval_exists(attr: dict | None, should_exist: bool) -> bool:
    """Evaluate an ``{"exists": true/false}`` condition."""
    if should_exist:
        return attr is not None
    return attr is None


def _eval_numeric(value: object, operators: list) -> bool:
    """Evaluate a ``{"numeric": [">=", 100, "<", 200]}`` condition.

    The *operators* list contains alternating (operator, operand) pairs.
    All pairs must match.
    """
    try:
        num_value = float(value)  # type: ignore[arg-type]
    except (TypeError, ValueError):
        return False

    i = 0
    while i < len(operators) - 1:
        op = operators[i]
        operand = float(operators[i + 1])
        if not _compare_numeric(num_value, op, operand):
            return False
        i += 2

    return True


def _compare_numeric(value: float, op: str, operand: float) -> bool:
    """Perform a single numeric comparison."""
    if op == "=":
        return value == operand
    if op == ">":
        return value > operand
    if op == ">=":
        return value >= operand
    if op == "<":
        return value < operand
    if op == "<=":
        return value <= operand
    return False


def _eval_anything_but(value: object, exclusions: list) -> bool:
    """Evaluate an ``{"anything-but": ["red"]}`` condition.

    Returns True when *value* does NOT match any of the *exclusions*.
    """
    str_value = str(value)
    return all(str_value != str(exc) for exc in exclusions)


# ------------------------------------------------------------------
# Attribute value extraction
# ------------------------------------------------------------------


def _extract_value(attr: object) -> object:
    """Extract the value from an SNS message attribute dict.

    SNS message attributes follow the shape
    ``{"DataType": "String", "StringValue": "red"}``.
    If *attr* is already a plain value, return it directly.
    """
    if isinstance(attr, dict):
        for key in ("StringValue", "BinaryValue"):
            if key in attr:
                return attr[key]
        # Fall back to the first value
        if attr:
            return next(iter(attr.values()))
    return attr

