562→  function buildServiceRequest(ctx) {
563→    const svc = ctx.service;
564→    const op = ctx.operation;
565→    const res = ctx.resource;
566→    const port = ctx.port;
567→    const base = 'http://localhost:' + port;
568→
569→    switch(svc) {
570→      case 'dynamodb': {
571→        const body = {};
572→        body.TableName = res.name;
573→        if (op === 'GetItem') body.Key = JSON.parse(val('f-key') || '{}');
574→        if (op === 'PutItem') body.Item = JSON.parse(val('f-item') || '{}');
575→        if (op === 'DeleteItem') body.Key = JSON.parse(val('f-key') || '{}');
576→        return {method:'POST', url:base+'/', headers:{
577→          'X-Amz-Target':'DynamoDB_20120810.'+op,
578→          'Content-Type':'application/x-amz-json-1.0'
579→        }, body:JSON.stringify(body)};
580→      }
581→      case 'sqs': {
582→        const qurl = res.queue_url || (base+'/000000000000/'+res.name);
583→        let params = 'Action='+op+'&QueueUrl='+encodeURIComponent(qurl);
584→        if (op === 'SendMessage') params += '&MessageBody='+encodeURIComponent(val('f-body')||'');
585→        return {method:'POST', url:base+'/', headers:{
586→          'Content-Type':'application/x-www-form-urlencoded'
587→        }, body:params};
588→      }
589→      case 's3': {
590→        const bucket = res.name;
591→        if (op === 'ListObjects')
592→          return {method:'GET', url:base+'/'+bucket+'?list-type=2', headers:{}, body:''};
593→        if (op === 'GetObject')
594→          return {method:'GET', url:base+'/'+bucket+'/'+val('f-key'), headers:{}, body:''};
595→        if (op === 'PutObject')
596→          return {method:'PUT', url:base+'/'+bucket+'/'+val('f-key'),
597→                  headers:{'Content-Type':'application/octet-stream'}, body:val('f-body')||''};
598→        if (op === 'DeleteObject')
599→          return {method:'DELETE', url:base+'/'+bucket+'/'+val('f-key'), headers:{}, body:''};
600→        break;
601→      }
602→      case 'sns': {
603→        const arn = res.arn || '';
604→        let params = 'Action='+op+'&TopicArn='+encodeURIComponent(arn);
605→        if (op === 'Publish') {
606→          params += '&Message='+encodeURIComponent(val('f-body')||'');
