611→          'Content-Type':'application/x-www-form-urlencoded'
612→        }, body:params};
613→      }
614→      case 'events': {
615→        let body = {};
616→        if (op === 'PutEvents') {
617→          let entry = JSON.parse(val('f-body')||'{}');
618→          if (!entry.EventBusName) entry.EventBusName = res.arn || res.name;
619→          body = {Entries:[entry]};
620→        } else if (op === 'ListRules') {
621→          body = {EventBusName: res.arn || res.name};
622→        }
623→        return {method:'POST', url:base+'/', headers:{
624→          'X-Amz-Target':'AWSEvents.'+op,
625→          'Content-Type':'application/x-amz-json-1.1'
626→        }, body:JSON.stringify(body)};
627→      }
628→      case 'stepfunctions': {
629→        let body = {};
630→        const arn = res.arn || '';
631→        if (op === 'StartExecution')
