635→        return {method:'POST', url:base+'/', headers:{
636→          'X-Amz-Target':'AWSStepFunctions.'+op,
637→          'Content-Type':'application/x-amz-json-1.0'
638→        }, body:JSON.stringify(body)};
639→      }
640→      case 'cognito-idp': {
641→        const poolId = res.user_pool_id || '';
642→        let body = {UserPoolId:poolId};
643→        return {method:'POST', url:base+'/', headers:{
644→          'X-Amz-Target':'AWSCognitoIdentityProviderService.'+op,
645→          'Content-Type':'application/x-amz-json-1.1'
646→        }, body:JSON.stringify(body)};
647→      }
648→      case 'apigateway': {
649→        const method = val('f-method') || 'GET';
650→        const path = val('f-path') || '/';
651→        const body = val('f-body') || '';
652→        return {method:method, url:base+path,
653→          headers:{'Content-Type':'application/json'}, body:body};
654→      }
655→    }
656→    return {method:'GET', url:base+'/', headers:{}, body:''};
