"""``lws dynamodb`` sub-commands."""

from __future__ import annotations

import asyncio
import json

import typer

from lws.cli.services.client import LwsClient, exit_with_error, output_json

app = typer.Typer(help="DynamoDB commands")

_SERVICE = "dynamodb"
_TARGET_PREFIX = "DynamoDB_20120810"


def _client(port: int) -> LwsClient:
    return LwsClient(port=port)


@app.command("put-item")
def put_item(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    item: str = typer.Option(..., "--item", help="JSON item"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Put an item into a table."""
    asyncio.run(_put_item(table_name, item, port))


async def _put_item(table_name: str, item_json: str, port: int) -> None:
    client = _client(port)
    try:
        parsed = json.loads(item_json)
    except json.JSONDecodeError as exc:
        exit_with_error(f"Invalid JSON in --item: {exc}")
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.PutItem",
            {"TableName": table_name, "Item": parsed},
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("get-item")
def get_item(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    key: str = typer.Option(..., "--key", help="JSON key"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Get an item from a table."""
    asyncio.run(_get_item(table_name, key, port))


async def _get_item(table_name: str, key_json: str, port: int) -> None:
    client = _client(port)
    try:
        parsed = json.loads(key_json)
    except json.JSONDecodeError as exc:
        exit_with_error(f"Invalid JSON in --key: {exc}")
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.GetItem",
            {"TableName": table_name, "Key": parsed},
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("delete-item")
def delete_item(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    key: str = typer.Option(..., "--key", help="JSON key"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Delete an item from a table."""
    asyncio.run(_delete_item(table_name, key, port))


async def _delete_item(table_name: str, key_json: str, port: int) -> None:
    client = _client(port)
    try:
        parsed = json.loads(key_json)
    except json.JSONDecodeError as exc:
        exit_with_error(f"Invalid JSON in --key: {exc}")
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.DeleteItem",
            {"TableName": table_name, "Key": parsed},
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("scan")
def scan(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    filter_expression: str = typer.Option(None, "--filter-expression", help="Filter expression"),
    expression_attribute_values: str = typer.Option(
        None, "--expression-attribute-values", help="JSON expression attribute values"
    ),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Scan a table."""
    asyncio.run(_scan(table_name, filter_expression, expression_attribute_values, port))


async def _scan(
    table_name: str,
    filter_expression: str | None,
    expression_attribute_values: str | None,
    port: int,
) -> None:
    client = _client(port)
    body: dict = {"TableName": table_name}
    if filter_expression:
        body["FilterExpression"] = filter_expression
    if expression_attribute_values:
        try:
            body["ExpressionAttributeValues"] = json.loads(expression_attribute_values)
        except json.JSONDecodeError as exc:
            exit_with_error(f"Invalid JSON in --expression-attribute-values: {exc}")
    try:
        result = await client.json_target_request(_SERVICE, f"{_TARGET_PREFIX}.Scan", body)
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("create-table")
def create_table(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    key_schema: str = typer.Option(..., "--key-schema", help="JSON key schema"),
    attribute_definitions: str = typer.Option(
        ..., "--attribute-definitions", help="JSON attribute definitions"
    ),
    global_secondary_indexes: str = typer.Option(
        None, "--global-secondary-indexes", help="JSON global secondary indexes"
    ),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Create a table."""
    asyncio.run(
        _create_table(table_name, key_schema, attribute_definitions, global_secondary_indexes, port)
    )


async def _create_table(
    table_name: str,
    key_schema_json: str,
    attribute_definitions_json: str,
    global_secondary_indexes_json: str | None,
    port: int,
) -> None:
    client = _client(port)
    body: dict = {"TableName": table_name}
    try:
        body["KeySchema"] = json.loads(key_schema_json)
    except json.JSONDecodeError as exc:
        exit_with_error(f"Invalid JSON in --key-schema: {exc}")
    try:
        body["AttributeDefinitions"] = json.loads(attribute_definitions_json)
    except json.JSONDecodeError as exc:
        exit_with_error(f"Invalid JSON in --attribute-definitions: {exc}")
    if global_secondary_indexes_json:
        try:
            body["GlobalSecondaryIndexes"] = json.loads(global_secondary_indexes_json)
        except json.JSONDecodeError as exc:
            exit_with_error(f"Invalid JSON in --global-secondary-indexes: {exc}")
    try:
        result = await client.json_target_request(_SERVICE, f"{_TARGET_PREFIX}.CreateTable", body)
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("delete-table")
def delete_table(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Delete a table."""
    asyncio.run(_delete_table(table_name, port))


async def _delete_table(table_name: str, port: int) -> None:
    client = _client(port)
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.DeleteTable",
            {"TableName": table_name},
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("describe-table")
def describe_table(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Describe a table."""
    asyncio.run(_describe_table(table_name, port))


async def _describe_table(table_name: str, port: int) -> None:
    client = _client(port)
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.DescribeTable",
            {"TableName": table_name},
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("list-tables")
def list_tables(
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """List all tables."""
    asyncio.run(_list_tables(port))


async def _list_tables(port: int) -> None:
    client = _client(port)
    try:
        result = await client.json_target_request(_SERVICE, f"{_TARGET_PREFIX}.ListTables", {})
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("query")
def query(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    key_condition_expression: str = typer.Option(
        ..., "--key-condition-expression", help="Key condition expression"
    ),
    expression_attribute_values: str = typer.Option(
        ..., "--expression-attribute-values", help="JSON expression attribute values"
    ),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Query a table."""
    asyncio.run(_query(table_name, key_condition_expression, expression_attribute_values, port))


async def _query(
    table_name: str,
    key_condition_expression: str,
    expression_attribute_values: str,
    port: int,
) -> None:
    client = _client(port)
    try:
        expr_values = json.loads(expression_attribute_values)
    except json.JSONDecodeError as exc:
        exit_with_error(f"Invalid JSON in --expression-attribute-values: {exc}")
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.Query",
            {
                "TableName": table_name,
                "KeyConditionExpression": key_condition_expression,
                "ExpressionAttributeValues": expr_values,
            },
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)

"""``lws s3api`` sub-commands."""

from __future__ import annotations

import asyncio
from pathlib import Path

import typer

from lws.cli.services.client import LwsClient, exit_with_error, output_json, xml_to_dict

app = typer.Typer(help="S3 API commands")

_SERVICE = "s3"


def _client(port: int) -> LwsClient:
    return LwsClient(port=port)


@app.command("put-object")
def put_object(
    bucket: str = typer.Option(..., "--bucket", help="Bucket name"),
    key: str = typer.Option(..., "--key", help="Object key"),
    body: str = typer.Option(..., "--body", help="File path for object body"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Upload an object to a bucket."""
    asyncio.run(_put_object(bucket, key, body, port))


async def _put_object(bucket: str, key: str, body_path: str, port: int) -> None:
    client = _client(port)
    try:
        await client.service_port(_SERVICE)
    except Exception as exc:
        exit_with_error(str(exc))
    path = Path(body_path)
    if not path.exists():
        exit_with_error(f"File not found: {body_path}")
    file_bytes = path.read_bytes()
    resp = await client.rest_request(_SERVICE, "PUT", f"{bucket}/{key}", body=file_bytes)
    result: dict = {"ETag": resp.headers.get("etag", "")}
    output_json(result)


@app.command("get-object")
def get_object(
    bucket: str = typer.Option(..., "--bucket", help="Bucket name"),
    key: str = typer.Option(..., "--key", help="Object key"),
    outfile: str = typer.Argument(None, help="Output file path"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Download an object from a bucket."""
    asyncio.run(_get_object(bucket, key, outfile, port))


async def _get_object(bucket: str, key: str, outfile: str | None, port: int) -> None:
    client = _client(port)
    try:
        await client.service_port(_SERVICE)
    except Exception as exc:
        exit_with_error(str(exc))
    resp = await client.rest_request(_SERVICE, "GET", f"{bucket}/{key}")
    if resp.status_code == 404:
        exit_with_error(f"Object not found: {key}")
    if outfile:
        Path(outfile).write_bytes(resp.content)
        output_json(
            {
                "ContentLength": len(resp.content),
                "ETag": resp.headers.get("etag", ""),
                "ContentType": resp.headers.get("content-type", ""),
            }
        )
    else:
        output_json(
            {
                "Body": resp.text,
                "ContentLength": len(resp.content),
                "ETag": resp.headers.get("etag", ""),
                "ContentType": resp.headers.get("content-type", ""),
            }
        )


@app.command("delete-object")
def delete_object(
    bucket: str = typer.Option(..., "--bucket", help="Bucket name"),
    key: str = typer.Option(..., "--key", help="Object key"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Delete an object from a bucket."""
    asyncio.run(_delete_object(bucket, key, port))


async def _delete_object(bucket: str, key: str, port: int) -> None:
    client = _client(port)
    try:
        await client.service_port(_SERVICE)
    except Exception as exc:
        exit_with_error(str(exc))
    await client.rest_request(_SERVICE, "DELETE", f"{bucket}/{key}")
    output_json({"DeleteMarker": True})


@app.command("list-objects-v2")
def list_objects_v2(
    bucket: str = typer.Option(..., "--bucket", help="Bucket name"),
    prefix: str = typer.Option("", "--prefix", help="Key prefix filter"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """List objects in a bucket."""
    asyncio.run(_list_objects_v2(bucket, prefix, port))


async def _list_objects_v2(bucket: str, prefix: str, port: int) -> None:
    client = _client(port)
    try:
        await client.service_port(_SERVICE)
    except Exception as exc:
        exit_with_error(str(exc))
    params: dict[str, str] = {}
    if prefix:
        params["prefix"] = prefix
    resp = await client.rest_request(_SERVICE, "GET", bucket, params=params)
    output_json(xml_to_dict(resp.text))


@app.command("head-object")
def head_object(
    bucket: str = typer.Option(..., "--bucket", help="Bucket name"),
    key: str = typer.Option(..., "--key", help="Object key"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Get object metadata."""
    asyncio.run(_head_object(bucket, key, port))


async def _head_object(bucket: str, key: str, port: int) -> None:
    client = _client(port)
    try:
        await client.service_port(_SERVICE)
    except Exception as exc:
        exit_with_error(str(exc))
    resp = await client.rest_request(_SERVICE, "HEAD", f"{bucket}/{key}")
    if resp.status_code == 404:
        exit_with_error(f"Object not found: {key}")
    output_json(
        {
            "ContentLength": resp.headers.get("content-length", "0"),
            "ContentType": resp.headers.get("content-type", ""),
            "ETag": resp.headers.get("etag", ""),
            "LastModified": resp.headers.get("last-modified", ""),
        }
    )


@app.command("create-bucket")
def create_bucket(
    bucket: str = typer.Option(..., "--bucket", help="Bucket name"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Create a bucket."""
    asyncio.run(_create_bucket(bucket, port))


async def _create_bucket(bucket: str, port: int) -> None:
    client = _client(port)
    try:
        await client.service_port(_SERVICE)
    except Exception as exc:
        exit_with_error(str(exc))
    resp = await client.rest_request(_SERVICE, "PUT", bucket)
    if resp.status_code >= 400:
        output_json(xml_to_dict(resp.text))
    else:
        output_json({"Location": f"/{bucket}"})


@app.command("delete-bucket")
def delete_bucket(
    bucket: str = typer.Option(..., "--bucket", help="Bucket name"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Delete a bucket."""
    asyncio.run(_delete_bucket(bucket, port))


async def _delete_bucket(bucket: str, port: int) -> None:
    client = _client(port)
    try:
        await client.service_port(_SERVICE)
    except Exception as exc:
        exit_with_error(str(exc))
    resp = await client.rest_request(_SERVICE, "DELETE", bucket)
    if resp.status_code >= 400:
        output_json(xml_to_dict(resp.text))
    else:
        output_json({})


@app.command("head-bucket")
def head_bucket(
    bucket: str = typer.Option(..., "--bucket", help="Bucket name"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Check if a bucket exists."""
    asyncio.run(_head_bucket(bucket, port))


async def _head_bucket(bucket: str, port: int) -> None:
    client = _client(port)
    try:
        await client.service_port(_SERVICE)
    except Exception as exc:
        exit_with_error(str(exc))
    resp = await client.rest_request(_SERVICE, "HEAD", bucket)
    if resp.status_code == 404:
        exit_with_error(f"Bucket not found: {bucket}")
    output_json(
        {
            "BucketRegion": resp.headers.get("x-amz-bucket-region", "us-east-1"),
        }
    )


@app.command("list-buckets")
def list_buckets(
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """List all buckets."""
    asyncio.run(_list_buckets(port))


async def _list_buckets(port: int) -> None:
    client = _client(port)
    try:
        await client.service_port(_SERVICE)
    except Exception as exc:
        exit_with_error(str(exc))
    resp = await client.rest_request(_SERVICE, "GET", "")
    output_json(xml_to_dict(resp.text))

"""``lws cognito-idp`` sub-commands."""

from __future__ import annotations

import asyncio

import typer

from lws.cli.services.client import LwsClient, exit_with_error, output_json

app = typer.Typer(help="Cognito Identity Provider commands")

_SERVICE = "cognito-idp"
_TARGET_PREFIX = "AWSCognitoIdentityProviderService"


def _client(port: int) -> LwsClient:
    return LwsClient(port=port)


@app.command("sign-up")
def sign_up(
    user_pool_name: str = typer.Option(..., "--user-pool-name", help="User pool name"),
    username: str = typer.Option(..., "--username", help="Username"),
    password: str = typer.Option(..., "--password", help="Password"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Sign up a new user."""
    asyncio.run(_sign_up(user_pool_name, username, password, port))


async def _sign_up(user_pool_name: str, username: str, password: str, port: int) -> None:
    client = _client(port)
    try:
        resource = await client.resolve_resource(_SERVICE, user_pool_name)
        client_id = resource.get("user_pool_id", "local-client-id")
    except Exception:
        client_id = "local-client-id"
    result = await client.json_target_request(
        _SERVICE,
        f"{_TARGET_PREFIX}.SignUp",
        {
            "ClientId": client_id,
            "Username": username,
            "Password": password,
        },
        content_type="application/x-amz-json-1.1",
    )
    output_json(result)


@app.command("confirm-sign-up")
def confirm_sign_up(
    user_pool_name: str = typer.Option(..., "--user-pool-name", help="User pool name"),
    username: str = typer.Option(..., "--username", help="Username"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Confirm a user sign-up."""
    asyncio.run(_confirm_sign_up(user_pool_name, username, port))


async def _confirm_sign_up(user_pool_name: str, username: str, port: int) -> None:
    client = _client(port)
    try:
        resource = await client.resolve_resource(_SERVICE, user_pool_name)
        client_id = resource.get("user_pool_id", "local-client-id")
    except Exception:
        client_id = "local-client-id"
    result = await client.json_target_request(
        _SERVICE,
        f"{_TARGET_PREFIX}.ConfirmSignUp",
        {
            "ClientId": client_id,
            "Username": username,
            "ConfirmationCode": "000000",
        },
        content_type="application/x-amz-json-1.1",
    )
    output_json(result)


@app.command("initiate-auth")
def initiate_auth(
    user_pool_name: str = typer.Option(..., "--user-pool-name", help="User pool name"),
    username: str = typer.Option(..., "--username", help="Username"),
    password: str = typer.Option(..., "--password", help="Password"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Authenticate a user."""
    asyncio.run(_initiate_auth(user_pool_name, username, password, port))


async def _initiate_auth(user_pool_name: str, username: str, password: str, port: int) -> None:
    client = _client(port)
    try:
        resource = await client.resolve_resource(_SERVICE, user_pool_name)
        client_id = resource.get("user_pool_id", "local-client-id")
    except Exception:
        client_id = "local-client-id"
    result = await client.json_target_request(
        _SERVICE,
        f"{_TARGET_PREFIX}.InitiateAuth",
        {
            "ClientId": client_id,
            "AuthFlow": "USER_PASSWORD_AUTH",
            "AuthParameters": {
                "USERNAME": username,
                "PASSWORD": password,
            },
        },
        content_type="application/x-amz-json-1.1",
    )
    output_json(result)


@app.command("create-user-pool")
def create_user_pool(
    pool_name: str = typer.Option(..., "--pool-name", help="User pool name"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
