    """List user pools."""
    asyncio.run(_list_user_pools(port))


async def _list_user_pools(port: int) -> None:
    client = _client(port)
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.ListUserPools",
