@app.command("describe-user-pool")
def describe_user_pool(
    user_pool_id: str = typer.Option(..., "--user-pool-id", help="User pool ID"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Describe a user pool."""
    asyncio.run(_describe_user_pool(user_pool_id, port))


async def _describe_user_pool(user_pool_id: str, port: int) -> None:
    client = _client(port)
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.DescribeUserPool",
            {"UserPoolId": user_pool_id},
            content_type="application/x-amz-json-1.1",
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)

"""``lws lambda`` sub-commands."""

from __future__ import annotations

import asyncio
import json
from pathlib import Path

import httpx
import typer

from lws.cli.services.client import exit_with_error, output_json

app = typer.Typer(help="Lambda commands")


@app.command("invoke")
def invoke(
    function_name: str = typer.Option(..., "--function-name", "-f", help="Lambda function name"),
    event: str = typer.Option(None, "--event", "-e", help="Inline JSON event payload"),
    event_file: Path = typer.Option(None, "--event-file", help="Path to JSON event file"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK management port"),
) -> None:
    """Invoke a Lambda function directly."""
    asyncio.run(_invoke(function_name, event, event_file, port))


async def _invoke(
    function_name: str,
    event_json: str | None,
    event_file: Path | None,
    port: int,
) -> None:
    event_payload = _resolve_event_payload(event_json, event_file)

    lambda_port = port + 9
    try:
        async with httpx.AsyncClient() as client:
            resp = await client.post(
                f"http://localhost:{lambda_port}/2015-03-31/functions/{function_name}/invocations",
                json=event_payload,
                timeout=30.0,
            )
            result = resp.json()
            output_json(result)
    except (httpx.ConnectError, httpx.ConnectTimeout):
        exit_with_error(f"Cannot reach ldk dev on port {port}. Is it running?")
    except Exception as exc:
        exit_with_error(f"Invocation failed: {exc}")


def _resolve_event_payload(event_json: str | None, event_file: Path | None) -> dict:
    """Parse event payload from CLI args."""
    if event_json is not None:
        try:
            return json.loads(event_json)
        except json.JSONDecodeError as exc:
            exit_with_error(f"Invalid JSON in --event: {exc}")

    if event_file is not None:
        if not event_file.exists():
            exit_with_error(f"Event file not found: {event_file}")
        try:
            return json.loads(event_file.read_text())
        except json.JSONDecodeError as exc:
            exit_with_error(f"Invalid JSON in event file: {exc}")

    return {}

"""E2E test for DynamoDB TransactWriteItems ConditionCheck."""

import json

import httpx


def _discover_dynamodb_port(e2e_port: int) -> int:
    """Discover the DynamoDB service port from the management endpoint."""
    resp = httpx.get(f"http://localhost:{e2e_port}/_ldk/resources", timeout=5.0)
    meta = resp.json()
    return int(meta["services"]["dynamodb"]["port"])


def _dynamodb_post(port: int, target: str, body: dict) -> httpx.Response:
    """Send a DynamoDB JSON-target request to the running server."""
    return httpx.post(
        f"http://localhost:{port}/",
        headers={
            "X-Amz-Target": f"DynamoDB_20120810.{target}",
            "Content-Type": "application/x-amz-json-1.0",
        },
        json=body,
        timeout=5.0,
    )


class TestTransactConditionCheck:
    def test_condition_check_pass_allows_writes(self, e2e_port, lws_invoke):
        # Arrange
        table_name = "e2e-transact-cc-pass"
        expected_data = "written"
        dynamodb_port = _discover_dynamodb_port(e2e_port)
        lws_invoke(
            [
                "dynamodb",
                "create-table",
                "--table-name",
                table_name,
                "--key-schema",
                '[{"AttributeName":"pk","KeyType":"HASH"}]',
                "--attribute-definitions",
                '[{"AttributeName":"pk","AttributeType":"S"}]',
                "--port",
                str(e2e_port),
            ]
        )
        lws_invoke(
            [
                "dynamodb",
                "put-item",
                "--table-name",
                table_name,
                "--item",
                json.dumps({"pk": {"S": "guard"}, "status": {"S": "ok"}}),
                "--port",
                str(e2e_port),
            ]
        )

        # Act
        resp = _dynamodb_post(
            dynamodb_port,
            "TransactWriteItems",
            {
                "TransactItems": [
                    {
                        "ConditionCheck": {
                            "TableName": table_name,
                            "Key": {"pk": {"S": "guard"}},
                            "ConditionExpression": "attribute_exists(pk)",
                        }
                    },
                    {
                        "Put": {
                            "TableName": table_name,
                            "Item": {
                                "pk": {"S": "new-item"},
                                "data": {"S": expected_data},
                            },
                        }
                    },
                ]
            },
        )

        # Assert
        expected_status_code = 200
        assert resp.status_code == expected_status_code
        get_resp = _dynamodb_post(
            dynamodb_port,
            "GetItem",
            {"TableName": table_name, "Key": {"pk": {"S": "new-item"}}},
        )
        actual_data = get_resp.json()["Item"]["data"]["S"]
        assert actual_data == expected_data

    def test_condition_check_fail_blocks_writes(self, e2e_port, lws_invoke):
        # Arrange
        table_name = "e2e-transact-cc-fail"
        dynamodb_port = _discover_dynamodb_port(e2e_port)
        lws_invoke(
            [
                "dynamodb",
                "create-table",
                "--table-name",
                table_name,
                "--key-schema",
                '[{"AttributeName":"pk","KeyType":"HASH"}]',
                "--attribute-definitions",
                '[{"AttributeName":"pk","AttributeType":"S"}]',
                "--port",
                str(e2e_port),
            ]
        )

        # Act
        resp = _dynamodb_post(
            dynamodb_port,
            "TransactWriteItems",
            {
                "TransactItems": [
                    {
                        "ConditionCheck": {
                            "TableName": table_name,
                            "Key": {"pk": {"S": "nonexistent"}},
                            "ConditionExpression": "attribute_exists(pk)",
                        }
                    },
                    {
                        "Put": {
                            "TableName": table_name,
                            "Item": {
                                "pk": {"S": "blocked"},
                                "data": {"S": "nope"},
                            },
                        }
                    },
                ]
            },
        )

        # Assert
        expected_status_code = 400
        assert resp.status_code == expected_status_code
        body = resp.json()
        expected_error = "com.amazonaws.dynamodb.v20120810#TransactionCanceledException"
        actual_error = body["__type"]
        assert actual_error == expected_error
        get_resp = _dynamodb_post(
            dynamodb_port,
            "GetItem",
            {"TableName": table_name, "Key": {"pk": {"S": "blocked"}}},
        )
        assert "Item" not in get_resp.json()

