 1→// Node.js invoker script for LDK Lambda runtime
 2→// Reads event from stdin, loads handler, invokes, writes result to stdout
 3→
 4→const fs = require('fs');
 5→
 6→async function main() {
 7→    const handlerSpec = process.env.LDK_HANDLER; // e.g., "index.handler"
 8→    const codePath = process.env.LDK_CODE_PATH;
 9→
10→    // Read event from stdin
11→    const input = fs.readFileSync('/dev/stdin', 'utf8');
12→    const event = JSON.parse(input);
13→
14→    // Parse handler spec: "file.function"
15→    const lastDot = handlerSpec.lastIndexOf('.');
16→    const modulePath = handlerSpec.substring(0, lastDot);
17→    const functionName = handlerSpec.substring(lastDot + 1);
18→
19→    // Build context from env
20→    const context = {
21→        functionName: process.env.AWS_LAMBDA_FUNCTION_NAME || 'local-function',
22→        functionVersion: '$LATEST',
23→        memoryLimitInMB: process.env.AWS_LAMBDA_FUNCTION_MEMORY_SIZE || '128',
24→        logGroupName: `/aws/lambda/${process.env.AWS_LAMBDA_FUNCTION_NAME || 'local-function'}`,
25→        logStreamName: 'local',
26→        awsRequestId: process.env.LDK_REQUEST_ID || 'local-request-id',
27→        invokedFunctionArn: process.env.LDK_FUNCTION_ARN || 'arn:ldk:lambda:local:000000000000:function:local',
28→        getRemainingTimeInMillis: () => 30000,
29→    };
30→
31→    // Load and invoke handler
32→    const fullPath = require('path').resolve(codePath, modulePath);
33→    const handler = require(fullPath)[functionName];
34→
35→    if (!handler) {
36→        throw new Error(`Handler function '${functionName}' not found in '${modulePath}'`);
37→    }
38→
39→    const result = await handler(event, context);
40→
41→    // Use synchronous write to fd 1 (stdout) to guarantee the data is
42→    // flushed before process.exit(). The callback-based process.stdout.write
43→    // can hang when stdout is a pipe and the event loop has pending work.
44→    fs.writeFileSync(1, JSON.stringify({result: result}));
45→    process.exit(0);
46→}
47→
48→main().catch(err => {
49→    fs.writeFileSync(1, JSON.stringify({
50→        error: {
51→            errorMessage: err.message,
52→            errorType: err.constructor.name,
53→            stackTrace: err.stack ? err.stack.split('\n') : []
54→        }
55→    }));
56→    process.exit(1);
57→});
