"""Management API for LDK.

Provides a FastAPI ``APIRouter`` mounted at ``/_ldk/`` that exposes endpoints
for invoking Lambda functions, resetting local state, querying provider
status, serving the web dashboard, and streaming logs via WebSocket.
"""

from __future__ import annotations

import uuid
from typing import Any

import httpx
from fastapi import APIRouter, Request, WebSocket, WebSocketDisconnect
from fastapi.responses import HTMLResponse, JSONResponse
from pydantic import BaseModel

from lws.api.gui import get_dashboard_html
from lws.interfaces import ICompute, InvocationResult, LambdaContext
from lws.interfaces.provider import Provider
from lws.logging.logger import get_logger, get_ws_handler
from lws.runtime.orchestrator import Orchestrator

_logger = get_logger("ldk.management")


class InvokeRequest(BaseModel):
    """Request body for the invoke endpoint."""

    function_name: str
    event: dict = {}


class InvokeResponse(BaseModel):
    """Response body for the invoke endpoint."""

    payload: dict | None = None
    error: str | None = None


class StatusResponse(BaseModel):
    """Response body for the status endpoint."""

    running: bool
    providers: list[dict[str, Any]]


async def _handle_invoke(
    request: InvokeRequest,
    compute_providers: dict[str, ICompute],
) -> JSONResponse:
    """Invoke a Lambda function by name with the given event."""
    compute = compute_providers.get(request.function_name)
    if compute is None:
        return JSONResponse(
            status_code=404,
            content={"error": f"Function not found: {request.function_name}"},
        )

    request_id = str(uuid.uuid4())
    context = LambdaContext(
        function_name=request.function_name,
        memory_limit_in_mb=128,
        timeout_seconds=30,
        aws_request_id=request_id,
        invoked_function_arn=(
            f"arn:aws:lambda:us-east-1:000000000000:function:{request.function_name}"
        ),
    )

    try:
        result: InvocationResult = await compute.invoke(request.event, context)
        _logger.info(
            "Invoked %s via management API -> %s",
            request.function_name,
            "OK" if result.error is None else "ERROR",
        )
        return JSONResponse(
            content={"payload": result.payload, "error": result.error},
        )
    except Exception as exc:
        _logger.error("Management invoke failed: %s", exc)
        return JSONResponse(status_code=500, content={"error": str(exc)})


async def _handle_reset(
    providers_map: dict[str, Provider],
) -> JSONResponse:
    """Reset all provider data and state."""
    _logger.info("Reset requested via management API")
    reset_count = 0

    for provider in providers_map.values():
        if hasattr(provider, "reset"):
            try:
                await provider.reset()
                reset_count += 1
            except Exception as exc:
                _logger.error("Error resetting %s: %s", provider.name, exc)

    return JSONResponse(content={"status": "ok", "providers_reset": reset_count})


async def _handle_status(
    orchestrator: Orchestrator,
    providers_map: dict[str, Provider],
) -> JSONResponse:
    """Return the status of all providers."""
    provider_list: list[dict[str, Any]] = []

    for node_id, provider in providers_map.items():
        try:
            healthy = await provider.health_check()
        except Exception:
            healthy = False

        provider_list.append({"id": node_id, "name": provider.name, "healthy": healthy})

    return JSONResponse(
        content={"running": orchestrator.running, "providers": provider_list},
    )


async def _handle_ws_logs(websocket: WebSocket) -> None:
    """Stream log entries to a WebSocket client."""
    await websocket.accept()
    handler = get_ws_handler()
    if handler is None:
        await websocket.close()
        return
    for entry in handler.backlog():
        await websocket.send_json(entry)
    q = handler.subscribe()
    try:
        while True:
            entry = await q.get()
            await websocket.send_json(entry)
    except WebSocketDisconnect:
        pass
    finally:
        handler.unsubscribe(q)


async def _handle_service_proxy(request: Request) -> JSONResponse:
    """Proxy requests to local service ports (avoids browser CORS)."""
    body = await request.json()
    url = body.get("url", "")
    if not url.startswith("http://localhost:"):
        return JSONResponse(
            status_code=400,
            content={"error": "Only localhost URLs are allowed"},
        )
    try:
        async with httpx.AsyncClient() as client:
            resp = await client.request(
                method=body.get("method", "GET"),
                url=url,
                headers=body.get("headers", {}),
                content=body.get("body", ""),
                timeout=10.0,
            )
        return JSONResponse(
            content={
                "status": resp.status_code,
                "headers": dict(resp.headers),
                "body": resp.text,
            }
        )
    except Exception as exc:
        return JSONResponse(status_code=502, content={"error": str(exc)})


def create_management_router(
    orchestrator: Orchestrator,
    compute_providers: dict[str, ICompute],
    providers: dict[str, Provider] | None = None,
    resource_metadata: dict[str, Any] | None = None,
) -> APIRouter:
    """Create a management API router.

    Args:
        orchestrator: The running ``Orchestrator`` instance.
        compute_providers: Map of function name to ``ICompute`` provider.
        providers: Optional map of all providers for status reporting.
        resource_metadata: Pre-built resource metadata for the ``/_ldk/resources`` endpoint.

    Returns:
        A FastAPI ``APIRouter`` to be included in the main application.
    """
    router = APIRouter(prefix="/_ldk", tags=["management"])
    all_providers = providers or orchestrator._providers
    _resource_metadata = resource_metadata or {}

    @router.post("/invoke")
    async def invoke_function(request: InvokeRequest) -> JSONResponse:
        return await _handle_invoke(request, compute_providers)

    @router.post("/reset")
    async def reset_state() -> JSONResponse:
        return await _handle_reset(all_providers)

    @router.get("/status")
    async def get_status() -> JSONResponse:
        return await _handle_status(orchestrator, all_providers)

    @router.get("/resources")
    async def get_resources() -> JSONResponse:
        return JSONResponse(content=_resource_metadata)

    @router.get("/gui")
    async def dashboard() -> HTMLResponse:
        return get_dashboard_html()

    @router.websocket("/ws/logs")
    async def ws_logs(websocket: WebSocket) -> None:
        await _handle_ws_logs(websocket)

    @router.post("/service-proxy")
    async def service_proxy(request: Request) -> JSONResponse:
        return await _handle_service_proxy(request)

    return router

"""``lws ssm`` sub-commands."""

from __future__ import annotations

import asyncio

import typer

from lws.cli.services.client import LwsClient, exit_with_error, output_json

app = typer.Typer(help="SSM Parameter Store commands")

_SERVICE = "ssm"
_TARGET_PREFIX = "AmazonSSM"


def _client(port: int) -> LwsClient:
    return LwsClient(port=port)


@app.command("put-parameter")
def put_parameter(
    name: str = typer.Option(..., "--name", help="Parameter name"),
    value: str = typer.Option(..., "--value", help="Parameter value"),
    param_type: str = typer.Option("String", "--type", help="String, StringList, or SecureString"),
    description: str = typer.Option("", "--description", help="Parameter description"),
    overwrite: bool = typer.Option(False, "--overwrite", help="Overwrite existing parameter"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Put a parameter value."""
    asyncio.run(_put_parameter(name, value, param_type, description, overwrite, port))


async def _put_parameter(
    name: str, value: str, param_type: str, description: str, overwrite: bool, port: int
) -> None:
    client = _client(port)
    try:
        body: dict = {"Name": name, "Value": value, "Type": param_type, "Overwrite": overwrite}
        if description:
            body["Description"] = description
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.PutParameter",
            body,
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("get-parameter")
def get_parameter(
    name: str = typer.Option(..., "--name", help="Parameter name"),
    with_decryption: bool = typer.Option(False, "--with-decryption", help="Decrypt SecureString"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Get a parameter value."""
    asyncio.run(_get_parameter(name, with_decryption, port))


async def _get_parameter(name: str, with_decryption: bool, port: int) -> None:
    client = _client(port)
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.GetParameter",
            {"Name": name, "WithDecryption": with_decryption},
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("get-parameters-by-path")
def get_parameters_by_path(
    path: str = typer.Option(..., "--path", help="Parameter path prefix"),
    recursive: bool = typer.Option(False, "--recursive", help="Recurse into sub-paths"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Get parameters by path prefix."""
    asyncio.run(_get_parameters_by_path(path, recursive, port))


async def _get_parameters_by_path(path: str, recursive: bool, port: int) -> None:
    client = _client(port)
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.GetParametersByPath",
            {"Path": path, "Recursive": recursive},
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("delete-parameter")
def delete_parameter(
    name: str = typer.Option(..., "--name", help="Parameter name"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
