"""Architecture test: E2E tests must use Gherkin / pytest-bdd pattern.

Each service directory must have a ``test_scenarios.py`` that loads all feature files
via ``scenarios("features/")``.  No ``Test*`` classes are allowed.
"""

from __future__ import annotations

import ast
from pathlib import Path

E2E_DIR = Path(__file__).parent.parent.parent.parent / "e2e"


class TestBddPattern:
    def test_e2e_test_files_use_scenarios(self):
        """Every test_*.py must call scenarios() from pytest_bdd."""
        # Arrange
        violations = []

        # Act
        for path in sorted(E2E_DIR.rglob("test_*.py")):
            source = path.read_text()
            if "scenarios(" not in source:
                rel = path.relative_to(E2E_DIR)
                violations.append(f"{rel} does not call scenarios()")

        # Assert
        assert (
            violations == []
        ), "E2E test files must use pytest-bdd scenarios() wiring:\n" + "\n".join(
            f"  - {v}" for v in violations
        )

    def test_e2e_test_files_have_no_test_classes(self):
        """No test_*.py file should contain a Test* class."""
        # Arrange
        violations = []

        # Act
        for path in sorted(E2E_DIR.rglob("test_*.py")):
            tree = ast.parse(path.read_text())
            classes = [
                node.name
                for node in ast.iter_child_nodes(tree)
                if isinstance(node, ast.ClassDef) and node.name.startswith("Test")
            ]
            if classes:
                rel = path.relative_to(E2E_DIR)
                violations.append(f"{rel} has classes: {classes}")

        # Assert
        assert (
            violations == []
        ), "E2E test files must not contain Test* classes (use BDD instead):\n" + "\n".join(
            f"  - {v}" for v in violations
        )

    def test_every_service_has_features_directory(self):
        """Every E2E service directory must have a features/ subdirectory."""
        # Arrange
        violations = []

        # Act
        for path in sorted(E2E_DIR.iterdir()):
            if not path.is_dir() or path.name.startswith("__"):
                continue
            features_dir = path / "features"
            if not features_dir.is_dir():
                violations.append(f"{path.name}/ missing features/ directory")

        # Assert
        assert (
            violations == []
        ), "E2E service directories must contain a features/ subdirectory:\n" + "\n".join(
            f"  - {v}" for v in violations
        )

    def test_every_service_has_steps_directory(self):
        """Every E2E service directory must have a steps/ subdirectory with __init__.py."""
        # Arrange
        violations = []

        # Act
        for path in sorted(E2E_DIR.iterdir()):
            if not path.is_dir() or path.name.startswith("__"):
                continue
            steps_dir = path / "steps"
            if not steps_dir.is_dir():
                violations.append(f"{path.name}/ missing steps/ directory")
            elif not (steps_dir / "__init__.py").exists():
                violations.append(f"{path.name}/steps/ missing __init__.py")

        # Assert
        assert (
            violations == []
        ), "E2E service directories must contain steps/ with __init__.py:\n" + "\n".join(
            f"  - {v}" for v in violations
        )

    def test_no_step_decorators_in_service_conftest(self):
        """Service-level conftest.py must not contain @given/@when/@then decorators."""
        # Arrange
        violations = []
        step_decorators = {"given", "when", "then"}

        # Act
        for path in sorted(E2E_DIR.iterdir()):
            if not path.is_dir() or path.name.startswith("__"):
                continue
            conftest = path / "conftest.py"
            if not conftest.exists():
                continue
            tree = ast.parse(conftest.read_text())
            for node in ast.walk(tree):
                if not isinstance(node, ast.FunctionDef):
                    continue
                for decorator in node.decorator_list:
                    name = None
                    if isinstance(decorator, ast.Call) and isinstance(decorator.func, ast.Name):
                        name = decorator.func.id
                    elif isinstance(decorator, ast.Name):
                        name = decorator.id
                    if name in step_decorators:
                        violations.append(
                            f"{path.name}/conftest.py has @{name}"
                            f" on {node.name} (line {node.lineno})"
                        )

        # Assert
        assert (
            violations == []
        ), "Service conftest.py must not contain step decorators (move to steps/):\n" + "\n".join(
            f"  - {v}" for v in violations
        )

    def test_conftest_injects_step_fixtures(self):
        """Every service conftest must import steps and inject step fixtures."""
        # Arrange
        violations = []

        # Act
        for path in sorted(E2E_DIR.iterdir()):
            if not path.is_dir() or path.name.startswith("__"):
                continue
            conftest = path / "conftest.py"
            if not conftest.exists():
                continue
            source = conftest.read_text()
            if "from . import steps" not in source:
                violations.append(f"{path.name}/conftest.py missing 'from . import steps'")

        # Assert
        assert violations == [], "Service conftest.py must import steps:\n" + "\n".join(
            f"  - {v}" for v in violations
        )

    def test_every_step_file_is_explicitly_imported(self):
        """Every .py in steps/ (except __init__) must be imported in __init__.py."""
        # Arrange
        violations = []

        # Act
        for path in sorted(E2E_DIR.iterdir()):
            if not path.is_dir() or path.name.startswith("__"):
                continue
            steps_dir = path / "steps"
            if not steps_dir.is_dir():
                continue
            init_file = steps_dir / "__init__.py"
            if not init_file.exists():
                continue
            init_source = init_file.read_text()

            # Wildcard imports are not allowed
            if "import *" in init_source:
                violations.append(f"{path.name}/steps/__init__.py uses wildcard import")

            for step_file in sorted(steps_dir.glob("*.py")):
                if step_file.name == "__init__.py":
                    continue
                module_name = step_file.stem
                # Accept single-line or multiline import forms
                single = f"from .{module_name} import {module_name}"
                multi = f"from .{module_name} import ("
                if single not in init_source and multi not in init_source:
                    violations.append(
                        f"{path.name}/steps/{step_file.name} not imported in __init__.py"
                    )

        # Assert
        assert (
            violations == []
        ), "Every step file must be explicitly imported in steps/__init__.py:\n" + "\n".join(
            f"  - {v}" for v in violations
        )

"""Architecture test: all Python files in test directories must follow naming conventions."""

from __future__ import annotations

import re
from pathlib import Path

TESTS_ROOT = Path(__file__).parent.parent.parent
TEST_DIRS = [
    TESTS_ROOT / "unit",
    TESTS_ROOT / "integration",
    TESTS_ROOT / "e2e",
]

ALLOWED_PATTERN = re.compile(r"^(__init__|conftest|test_.+|_helpers)\.py$")


class TestFileNaming:
    def test_all_test_files_follow_naming_conventions(self):
        violations = []
        for test_dir in TEST_DIRS:
            if not test_dir.exists():
                continue
            for path in sorted(test_dir.rglob("*.py")):
                # Files inside steps/ directories follow step-name conventions
                if "steps" in path.parts:
                    continue
                if not ALLOWED_PATTERN.match(path.name):
                    rel = path.relative_to(TESTS_ROOT)
                    violations.append(str(rel))

        assert (
            violations == []
        ), "Test files must be __init__.py, conftest.py, or test_*.py:\n" + "\n".join(
            f"  - {v}" for v in violations
        )

